mapper.insert:
insert into product
SET p_title = #{title}
, p_name = #{name}
, p_price = #{price}
, p_quantity = #{quantity}
, description = #{description}
, allergy = #{allergy}
, is_delete = 0

mapper.update:
update product A
inner join product_category_relation B
ON A.product_id = B.product_id
SET A.p_title = #{title}
, A.p_name = #{name}
, A.p_price = #{price}
, A.p_quantity = #{quantity}
, A.description = #{description}
, A.sale_volumn = #{saleVolume}
, A.allergy = #{allergy}
, A.created_at = current_timestamp()
, B.cate_id = #{category}
where A.product_id = #{productId}

update product
SET p_title = #{title}
, p_name = #{name}
, p_price = #{price}
, p_quantity = #{quantity}
, description = #{description}
, sale_volumn = #{saleVolume}
, allergy = #{allergy}
, updated_at = current_timestamp()
where product_id = #{productId}

update product
SET is_delete = 1
where product_id = #{productId}

update product
set p_title=#{title}
,p_name=#{name}
,p_price=#{price}
,p_quantity=#{quantity}
,description=#{description}
,allergy=#{allergy}
, category = #{category}
, cateDetail = #{category_detail};
where product_id=#{productId}

mapper.insert:
insert into product_img
set img = #{img},
product_id = #{productId}

mapper.select:
select product_id productId,img
from product_img
