mapper.insert:
insert into product
SET p_title = #{title}
, p_name = #{name}
, p_price = #{price}
, p_quantity = #{quantity}
, description = #{description}
, allergy = #{allergy}
, is_delete = 0

mapper.update:
update product
SET p_title = #{title}
, p_name = #{name}
, p_price = #{price}
, p_quantity = #{quantity}
, description = #{description}
, sale_volumn = #{saleVolume}
, allergy = #{allergy}
, created_at = current_timestamp()
where product_id = #{productId}

