mapper.insert:
insert into product
SET p_title = #{title}
, p_name = #{name}
, p_price = #{price}
, p_quantity = #{quantity}
, description = #{description}
, allergy = #{allergy}
, is_delete = 0

mapper.select:
select p_title title, p_name name, p_price price, p_quantity quantity, description
from product
where product_iD = #{productId}

mapper.update:
update product
SET p_title = #{title}
, p_name = #{name}
, p_price = #{price}
, p_quantity = #{quantity}
, description = #{description}
, sale_volumn = #{saleVolume}
, allergy = #{allergy}
, updated_at = current_timestamp()
where product_id = #{productId}

update product
SET is_delete = 1
where product_id = #{productId}

