mapper.select:
SELECT A.product_id productId,A.p_title title,A.p_name name,A.p_price price,A.p_quantity quantity,B.thumbnail thumbnail,A.sale_volumn volumn from product A left join product_img B ON A.product_id=B.product_id LEFT JOIN product_allergy C ON A.product_id=C.product_id where A.p_quantity!=0 AND A.is_delete=0
AND A.product_id NOT IN(SELECT product_id FROM product_allergy WHERE allergy_id in(${strallergy}))
group by A.product_id ORDER BY A.created_at desc LIMIT #{startIdx},#{row};

select A.product_id productId,A.p_title title,A.p_name name,A.p_price price,A.p_quantity quantity,B.thumbnail thumbnail,A.sale_volumn volumn from product A left join product_img B ON A.product_id=B.product_id LEFT JOIN product_allergy C ON A.product_id=C.product_id where A.p_quantity!=0 and A.is_delete=0
AND A.product_id NOT IN(SELECT product_id FROM product_allergy WHERE allergy_id in(${strallergy}))
group by A.product_id order by A.sale_volumn desc limit #{startIdx},#{row}

select COUNT(distinct A.product_id) from product A left join product_img B ON A.product_id=B.product_id LEFT JOIN product_allergy C ON A.product_id=C.product_id where A.p_quantity!=0 and A.is_delete=0
AND A.product_id NOT IN(SELECT product_id FROM product_allergy WHERE allergy_id in(${strallergy}))
order by A.sale_volumn DESC;

SELECT TIMESTAMPDIFF(MONTH, birthday, date(NOW()))
FROM user
WHERE iuser=#{iuser}

select A.product_id productId,A.p_title title,A.p_name name,A.p_price price,A.p_quantity quantity,B.thumbnail thumbnail,A.sale_volumn volumn from product A left join product_img B ON A.product_id=B.product_id LEFT JOIN product_category_relation C ON A.product_id=C.product_id LEFT JOIN product_allergy D ON A.product_id=D.product_id WHERE C.cate_id=#{cate} and A.p_quantity!=0 and A.is_delete=0
AND A.product_id NOT IN(SELECT product_id FROM product_allergy WHERE allergy_id in(${strallergy}))
group by A.product_id order by A.sale_volumn desc,rand() LIMIT #{row}
