mapper.insert:
INSERT INTO user
SET email = #{email}
, password = #{password}
, name = #{name}
, mobile_nb = #{mobileNb}
, role = 'ROLE_USER'
, zip_code = #{zipCode}
, address = #{address}
, address_detail = #{addressDetail}
, nick_nm = #{nickNm}

mapper.select:
SELECT iuser, email, password, name, role, secret_key secretKey
FROM user
WHERE email = #{email}

mapper.update:
UPDATE user
SET secret_key = #{secretKey}
WHERE iuser = #{iuser}

mapper.insert:
INSERT INTO user_token
(iuser, ip, access_token, refresh_token)
VALUES
(#{iuser}, #{ip}, #{accessToken}, #{refreshToken})

ON DUPLICATE KEY UPDATE
ip = #{ip}
, access_token = #{accessToken}
, refresh_token = #{refreshToken}

mapper.select:
SELECT iuser, ip, access_token accessToken, refresh_token refreshToken, created_at createdAt, updated_at updatedAt
FROM user_token
WHERE iuser = #{iuser} AND ip = #{ip}
